package com.homework;

public class PatternsPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 for(int e=1;e<5;e++)
		   {
		   int alphabet=65;                          //value of A
		   for(int j=1;j<=e;j++)
		   {
		   System.out.print((char)alphabet);
		   alphabet++;
		   }
		   System.out.println();
		   }	
		 }

}
//A
//ABC
//ABCD